// EditarUsuarioModal.js

import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const EditarUsuarioModal = ({ show, handleClose, identificacion }) => {
  const [datosUsuario, setDatosUsuario] = useState({
    Nombres: '',
    Apellidos: '',
    Identificacion: '',
    Direccion: '',
    Telefono: '',
    Correo: '',
    Clave: '',
    Rol: '',
  });

  const roles = ['Docente', 'Administrativo', 'Estudiante', 'Brigadista'];
  const [error, setError] = useState('');

  useEffect(() => {
    // Aquí realizamos la solicitud al backend para obtener los datos del usuario por su identificación
    fetch(`http://localhost:8095/api/v1/personas/obtener/identificacion/${identificacion}`)
      .then(response => response.json())
      .then(data => {
        // Actualizamos el estado con los datos del usuario recibidos del backend
        setDatosUsuario(data.data);
      })
      .catch(error => {
        console.error('Error al obtener datos del usuario:', error);
      });
  }, [identificacion]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setDatosUsuario({
      ...datosUsuario,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8095/api/v1/personas/editar/${datosUsuario.External}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          tipo_persona: datosUsuario.Rol,
          cuenta: {
            correo: datosUsuario.Correo,
            clave: datosUsuario.Clave,
          },
          nombres: datosUsuario.Nombres,
          apellidos: datosUsuario.Apellidos,
          direccion: datosUsuario.Direccion,
          identificacion: datosUsuario.Identificacion,
          telefono: datosUsuario.Telefono,
        }),
      });
      const data = await response.json();

      if (response.ok) {
        console.log('Usuario editado correctamente:', data);
        handleClose();
      } else {
        console.error('Error al editar usuario:', data);
        setError(data.data.evento);
      }
    } catch (error) {
      console.error('Error al enviar la solicitud:', error);
      setError('Error al enviar la solicitud');
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar Usuario</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formNombres">
            <Form.Label>Nombres:</Form.Label>
            <Form.Control
              type="text"
              placeholder="Ingresa nombres"
              name="Nombres"
              value={datosUsuario.Nombres}
              onChange={handleInputChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="formApellidos">
            <Form.Label>Apellidos:</Form.Label>
            <Form.Control
              type="text"
              placeholder="Ingresa apellidos"
              name="Apellidos"
              value={datosUsuario.Apellidos}
              onChange={handleInputChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="formIdentificacion">
            <Form.Label>Identificación:</Form.Label>
            <Form.Control
              type="text"
              placeholder="Ingresa identificación"
              name="Identificacion"
              value={datosUsuario.Identificacion}
              onChange={handleInputChange}
              readOnly // Campo de solo lectura
              required
            />
          </Form.Group>
          <Form.Group controlId="formDireccion">
            <Form.Label>Dirección:</Form.Label>
            <Form.Control
              type="text"
              placeholder="Ingresa dirección"
              name="Direccion"
              value={datosUsuario.Direccion}
              onChange={handleInputChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="formTelefono">
            <Form.Label>Teléfono:</Form.Label>
            <Form.Control
              type="text"
              placeholder="Ingresa teléfono"
              name="Telefono"
              value={datosUsuario.Telefono}
              onChange={handleInputChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="formCorreo">
            <Form.Label>Correo Electrónico:</Form.Label>
            <Form.Control
              type="email"
              placeholder="Ingresa correo electrónico"
              name="Correo"
              value={datosUsuario.Correo}
              onChange={handleInputChange}
              readOnly // Campo de solo lectura
              required
            />
          </Form.Group>
          <Form.Group controlId="formClave">
            <Form.Label>Clave:</Form.Label>
            <Form.Control
              type="password"
              placeholder="Ingresa clave"
              name="Clave"
              value={datosUsuario.Clave}
              onChange={handleInputChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="formRol">
            <Form.Label>Rol:</Form.Label>
            <Form.Control
              as="select"
              value={datosUsuario.Rol}
              onChange={handleInputChange}
              name="Rol"
              required
            >
              <option value="">Selecciona un rol</option>
              {roles.map((rol, index) => (
                <option key={index} value={rol}>
                  {rol}
                </option>
              ))}
            </Form.Control>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>Cancelar</Button>
        <Button variant="primary" onClick={handleSubmit}>Guardar Cambios</Button>
      </Modal.Footer>
      {error && (
        <p className="text-danger mt-3">
          {error === 'La identificación ya está registrada' ? 'La identificación ya está registrada.' :
            error === 'El teléfono ya está registrado' ? 'El teléfono ya está registrado.' :
              error === 'El correo ya está registrado' ? 'El correo electrónico ya está registrado.' :
                'Ocurrió un error. Por favor, inténtalo de nuevo más tarde.'}
        </p>
      )}
    </Modal>
  );
};

export default EditarUsuarioModal;
