import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const MenuBar = () => {
    const [token, setToken] = useState(null); // Estado del token
    const [rol, setRol] = useState(null); // Estado del rol del usuario
    const history = useNavigate();

    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        const storedRol = localStorage.getItem('rol');
        if (storedToken) {
            setToken(storedToken);
            setRol(storedRol);
        }
    }, []);

    const handleCerrarSesion = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('rol');
        history('/map');
        window.location.reload(); // Refrescar la página
    };

    const handleMapaClick = () => {
        history('/map');
        window.location.reload(); // Refrescar la página
    };

    const handleModificarPlanEmergenciaClick = () => {
        window.open('https://drive.google.com/drive/folders/1ZXYLhbc-Mjte8jFfV5RYAWP5UczolTXG?usp=sharing', '_blank');
        window.location.reload(); // Refrescar la página
    };

    const handleIniciarSesionClick = () => {
        history('/');
    };

    if (!token) {
        return (
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container">
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleIniciarSesionClick}>
                        Iniciar Sesión
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleModificarPlanEmergenciaClick}>
                        Plan de Emergencia
                    </button>
                </div>
            </nav>
        );
    }

    if (rol === 'administrador') {
        return (
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container">
                    <button className="nav-link btn btn-outline-primary mx-2">
                        Agregar Nodo
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2">
                        Agregar Ruta
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2">
                        Gestionar Usuario
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2">
                        Gestionar Nodo
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleCerrarSesion}>
                        Cerrar Sesión
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleMapaClick}>
                        Mapa
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleModificarPlanEmergenciaClick}>
                        Plan de Emergencia
                    </button>
                </div>
            </nav>
        );
    }

    if (rol === 'brigadista') {
        return (
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container">
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleCerrarSesion}>
                        Cerrar Sesión
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleMapaClick}>
                        Mapa
                    </button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={handleModificarPlanEmergenciaClick}>
                        Plan de Emergencia
                    </button>
                </div>
            </nav>
        );
    }

    // Si el rol no es administrador ni brigadista, se muestra un mensaje de error y se redirige a la página principal
    history('/');
    return null;
};

export default MenuBar;
